import numpy as np
import time

def search(arr, checkFunction, transformFunc=lambda a: a):
    for item in arr:
        transformed_item = transformFunc(item)
        if checkFunction(transformed_item):
            return item
    return None

def fun(value):
    time.sleep(0.1)
    return value == 20

def transform(value):
    return value*10 if value !=None else None 


